// notif.js
import React, { useState } from 'react';

const Notif = () => {
  const [notifikasi, setNotifikasi] = useState([
    { id: 1, title: 'Jadwal Keamanan Baru Masuk', status: 'belum' },
    { id: 2, title: 'Jadwal Keamanan Baru Masuk', status: 'sudah' },
  ]);

  const handleStatusChange = (id, status) => {
    setNotifikasi(prevNotifikasi => prevNotifikasi.map(notif => {
      if (notif.id === id) {
        return { ...notif, status };
      }
      return notif;
    }));
  };

  return (
    <div className="notif-container">
      <h2>NOTIFIKASI</h2>
      <div className="notif-buttons">
        <button>SEMUA</button>
        <button>BELUM TERBACA</button>
        <button>SUDAH TERBACA</button>
      </div>
      <p>
        {notifikasi.filter(notif => notif.status === 'belum').length} Notifikasi belum terbaca dan 
        {notifikasi.filter(notif => notif.status === 'sudah').length} Notifikasi sudah terbaca
      </p>
      <div className="notif-list">
        <h3>Hari Ini</h3>
        {notifikasi.map(notif => (
          <div className="notif-item" key={notif.id}>
            <span>{notif.title}</span>
            <button onClick={() => handleStatusChange(notif.id, 'sudah')} disabled={notif.status === 'sudah'}>
              {notif.status === 'sudah' ? 'Sudah Terbaca' : 'Tandai Terbaca'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notif;
