import React, { useState } from 'react';

function Notification() {
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      title: 'Jadwal keamanan baru masuk',
      content: 'Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s, when an unknown printer took a',
      time: '09.20',
      read: false,
    },
    {
      id: 2,
      title: 'Jadwal keamanan baru masuk',
      content: 'Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s, when an unknown printer took a',
      time: '09.20',
      read: false,
    },
    {
      id: 3,
      title: 'Jadwal keamanan baru masuk',
      content: 'Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s, when an unknown printer took a',
      time: '09.20',
      read: true,
    },
    {
      id: 4,
      title: 'Jadwal keamanan baru masuk',
      content: 'Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s, when an unknown printer took a',
      time: '09.20',
      read: true,
    },
  ]);

  const markAsRead = (id) => {
    setNotifications(prevNotifications => prevNotifications.map(notification =>
      notification.id === id ? { ...notification, read: true } : notification
    ));
  };

  return (
    <div className="container">
      <h1>NOTIFIKASI</h1>
      <div className="buttons">
        <button>SEMUA</button>
        <button>BELUM TERBACA</button>
        <button>SUDAH TERBACA</button>
      </div>
      <p>1 Notifikasi belum terbaca dan 2 Notifikasi sudah terbaca</p>
      <div className="notification-list">
        <h2>Hari Ini</h2>
        {notifications
          .filter(notification => notification.time.startsWith('09.20'))
          .map(notification => (
            <div
              key={notification.id}
              className={`notification ${notification.read ? 'read' : ''}`}
              onClick={() => markAsRead(notification.id)}
            >
              <h3>{notification.title}</h3>
              <p>{notification.content}</p>
              <span className="time">{notification.time}</span>
            </div>
          ))}
        <h2>Kemarin</h2>
        {notifications
          .filter(notification => notification.time.startsWith('09.20'))
          .map(notification => (
            <div
              key={notification.id}
              className={`notification ${notification.read ? 'read' : ''}`}
              onClick={() => markAsRead(notification.id)}
            >
              <h3>{notification.title}</h3>
              <p>{notification.content}</p>
              <span className="time">{notification.time}</span>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Notification;
